import React from "react";
import { useTranslation } from "react-i18next";
import { Card, Col, Row } from "react-bootstrap";

const MyComponent = () => {
  const { t } = useTranslation();

  const items = [
    // { id: Math.random().toString(), title: t("Lunch"), description: t("text"), price: t("price"), imgUrl: "1.png" },
    // { id: Math.random().toString(), title: t("Breakfast"), description: t("text2"), price: t("price"), imgUrl: "2.png" },
    // { id: Math.random().toString(), title: t("Breakfast"), description: t("text3"), price: t("price"), imgUrl: "3.png" },
    // { id: Math.random().toString(), title: t("Lunch"), description: t("text"), price: t("price"), imgUrl: "1.png" },
    // { id: Math.random().toString(), title: t("Breakfast"), description: t("text3"), price: t("price"), imgUrl: "3.png" },
    // { id: Math.random().toString(), title: t("Dinner"), description: t("text2"), price: t("price"), imgUrl: "2.png" },
    // { id: Math.random().toString(), title: t("Lunch"), description: t("text"), price: t("price"), imgUrl: "1.png" },
  ];

  return (
    <Row>
      {items.length >= 1 ? (
        items.map((item) => (
          <CardItem
            key={item.id}
            title={item.title}
            imgUrl={item.imgUrl}
            price={item.price}
            description={item.description}
          />
        ))
      ) : (
        <h3 className="text-center">لا يوجد اصناف</h3>
      )}
    </Row>
  );
};

const CardItem = ({ title, description, imgUrl, price }) => {
  return (
    <Col sm="12" className="mb-3">
      <Card className="d-flex flex-row" style={{ backgroundColor: '#F8F8F8' }}>
        <Card.Img className="img-item" variant="top" src={imgUrl} />
        <Card.Body>
          <Card.Title className="d-flex justify-content-between">
            <div className="item-title">{title}</div>
            <div className="item-price">{price}</div>
          </Card.Title>
          <Card.Text className="py-2">
            <div className="item-description"></div>
          </Card.Text>
          <div className="item-category">{description}</div>
        </Card.Body>
      </Card>
    </Col>
  );
};

export default MyComponent;
