import React, { useState } from "react";
import { Button, Col, Row } from "react-bootstrap";
import { useTranslation } from "react-i18next";

const Hi = ({ filterByCategory, filterByName, items }) => {
  const { t } = useTranslation();
  const [searchValue, setSearchValue] = useState("");

  const onFilter = (category) => {
    filterByCategory(category);
  };

  const onSearch = () => {
    filterByName(searchValue);
  };

  return (
    <Row className="my-2 mb-5">
      <Col sm="12" className="d-flex justify-content-center">
        <div>
          <Button className="btn mx-2" onClick={() => onFilter("all")}>
            {t("all")}
          </Button>
          <Button className="btn mx-2" onClick={() => onFilter("Breakfast")}>
            {t("Breakfast")}
          </Button>
          <Button className="btn mx-2" onClick={() => onFilter("Lunch")}>
            {t("Lunch")}
          </Button>
          <Button className="btn mx-2" onClick={() => onFilter("Dinner")}>
            {t("Dinner")}
          </Button>
          <input
            type="text"
            placeholder="Search by name"
            value={searchValue}
            onChange={(e) => setSearchValue(e.target.value)}
          />
          <Button className="btn mx-2" onClick={onSearch}>
            Search
          </Button>
        </div>
      </Col>
      <Col sm="12" className="d-flex justify-content-center">
        <هtemList items={items} />
      </Col>
    </Row>
  );
};

export default Hi;
