import React, { useState } from "react";
import NavScrollExample from "./components/NavBar";
import Header from "./components/Header";
import Hi from "./components/catgory";
import ItemList from "./components/ItemList";
import { items } from "./data";

function App() {
  const [itemsData, setItemsData] = useState(items);
  const { t } = useTranslation();

  const filterByCategory = (category) => {
    if (category === "الكل") {
      setItemsData(items); // Reset to the original items when "الكل" category is selected
    } else {
      const newArr = items.filter((item) => item.category === category);
      setItemsData(newArr); // Update itemsData with the filtered items based on the selected category
    }
  };

  return (
    <div className="App">
      <NavScrollExample />
      <Header />
      <Hi filterByCategory={filterByCategory} items={items} />
      <ItemList items={itemsData}/>
    </div>
  );
}

export default App;
