import React, { useState } from "react";
import NavScrollExample from "./components/NavBar";
import Header from "./components/Header";
import Hi from "./components/catgory";
import ItemList from "./components/ItemList";
// import { items } from "./data";

function App() {
  const [filteredItems, setFilteredItems] = useState(items);

  // Filter by category
  const filterByCategory = (category) => {
    if (category === "all") {
      setFilteredItems(items);
    } else {
      const newArr = items.filter((item) => item.category === category);
      setFilteredItems(newArr);
    }
  };

  // Filter by name
  const filterByName = (name) => {
    const newArr = items.filter((item) =>
      item.title.toLowerCase().includes(name.toLowerCase())
    );
    setFilteredItems(newArr);
  };
  return (
    <div className="App">
      <NavScrollExample />
      <Header />
      <Hi  filterByCategory={filterByCategory} filterByName={filterByName} />
      <ItemList items={filteredItems}/>
    </div>
  );
}

export default App;
