import React, { useState } from "react";
import NavScrollExample from "./components/NavBar";
import Header from "./components/Header";
import Hi from "./components/catgory";
import ItemList from "./components/ItemList";
import { items } from "./data";
import { useTranslation } from "react-i18next";

function App() {
  const { t } = useTranslation();
  const [itemsData, setItemsData] = useState(items);

  const filterByCategory = (category) => {
    if (category === t("all")) {
      setItemsData(items); // إعادة تعيين القائمة للقيمة الأصلية عند اختيار الفئة "الكل"
    } else {
      const newArr = items.filter((item) => item.category === category);
      setItemsData(newArr); // تحديث قائمة العناصر بناءً على الفئة المحددة
    }
  };

  return (
    <div className="App">
      <NavScrollExample />
      <Header />
      <Hi filterByCategory={filterByCategory} items={items} />
      <ItemList items={itemsData}/>
    </div>
  );
}

export default App;
