import React from 'react';
import { Row, Col, Card } from 'react-bootstrap';
import { useTranslation } from 'react-i18next';
import arJson from "../json/ar.json";
import erJson from "../json/en.json";


const ItemsList = ({ itemsData }) => {
  const { t } = useTranslation();

  return (
    <Row>
      {itemsData.length >= 1 ? (
        itemsData.map((item) => (
          <Col key={item.id} sm="12" className="mb-3">
            <Card className="d-flex flex-row" style={{ backgroundColor: '#F8F8F8' }}>
              <Card.Img className="img-item" variant="top" src={item.imgUrl} />
              <Card.Body>
                <Card.Title className="d-flex justify-content-between">
                  <div className="item-title">
                    {t(`cardTitle_${item.id}`) || [t.language]?.[`cardTitle_${item.id}`]}
                  </div>
                  <div className="item-price">
                    {item.price} {t('price')}
                  </div>
                </Card.Title>
                <Card.Text className="py-2">
                  <div className="item-description">
                    {t(`text${item.id}`) || arJson,erJson[t.language]?.[`${item.id}`]}
                  </div>
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))
      ) : (
        <h3 className="text-center">{t('noItems')}</h3>
      )}
    </Row>
  );
};

export default ItemsList;
