import React from "react";
import { Button, Col, Row } from "react-bootstrap";
import { useTranslation } from "react-i18next";

const Hi = ({ filterbyCategory, allCategory }) => {
  const onFliter = (cat) => {
    filterbyCategory(cat);
  };
  const { t } = useTranslation();
  return (
    <Row className="my-2 mb-5">
      <Col sm="12" className="d-flex justify-content-center ">
        <div>
          <Button className="btn mx-2">{t("all")}</Button>
          <Button onClick={() => onFliter("Breakfast")} className="btn mx-2">
            {t("Breakfast")}
          </Button>
          <Button className="btn mx-2">{t("Lunch")}</Button>
          <Button className="btn mx-2">{t("Dinner")}</Button>
        </div>
      </Col>
    </Row>
  );
};

export default Hi;
