import I18n from 'i18n';
import { initReactI18next } from 'react-i18next';
import arTranslation from './translations/ar.json';
import enTranslation from './translations/en.json';

// قم بتهيئة i18n
i18n.use(initReactI18next).init({
  lng: 'en', // اللغة الافتراضية (يمكن تغييرها إلى 'ar' للعربية)
  fallbackLng: 'en', // اللغة الاحتياطية إذا لم يتم العثور على الترجمة المطلوبة
  resources: {
    en: {
      translation: enTranslation, // الترجمة باللغة الإنجليزية
    },
    ar: {
      translation: arTranslation, // الترجمة باللغة العربية
    },
  },
});

export default I18n;
