import React from "react";
import { Card, Col, Row } from "react-bootstrap";
import { useTranslation } from "react-i18next";

const ItemList = ({ itemsData }) => {
    const { t } = useTranslation();

    const screws = ["Lunch", "Dinner", "Snacks"];
  
    const items = [
      { id: Math.random().toString(), title: "", description: " ", price: " ", category: "Category 1", imgUrl: "1.png" },
      { id: Math.random().toString(), title: " ", description: " ", price: " ", category: "Category 2", imgUrl: "2.png" },
      { id: Math.random().toString(), title: "", description: " ", price: " ", category: "Category 3", imgUrl: "3.png" }
    ];
  
    return (
      <Row>
        {items.map((item) => (
          <Col key={item.id} sm="12" className="mb-3">
            <Card className="d-flex flex-row" style={{ backgroundColor: '#F8F8F8' }}>
              <Card.Img className="img-item" variant="top" src={item.imgUrl} />
              <Card.Body>
                <Card.Title className="d-flex justify-content-between">
                  <div className="item-title">{item.title}</div>
                  <div className="item-price">{item.price}</div>
                </Card.Title>
                <Card.Text className="py-2">
                  <div className="item-description">{item.description}</div>
                </Card.Text>
                {screws.map((screw, index) => (
                  <div className="item-title" key={index}>
                    {item.title} {t(screw)}
                  </div>
                ))}
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    );
  };
  
export default ItemList;
