import React from "react";
import { Card, Col, Row } from "react-bootstrap";
import { useTranslation } from "react-i18next";

const ItemList = ({ itemsData }) => {
  const { t } = useTranslation();
  
  return (
    <Row>
      {itemsData && itemsData.length >= 1 ? (
        itemsData.map((item) => {
          const itemTitle = item.title + t("all") + t("Breakfast") + t("Lunch") + t("Dinner");
          const itemPrice = item.price + " " + t("price");
          const itemDescription = item.description + t("text") + t("text2") + t("text3");

          return (
            <Col key={item.id} sm="12" className="mb-3">
              <Card className="d-flex flex-row" style={{ backgroundColor: '#F8F8F8' }}>
                <Card.Img className="img-item" variant="top" src={item.imgUrl} />
                <Card.Body>
                  <Card.Title className="d-flex justify-content-between">
                    <div className="item-title">{itemTitle}</div>
                    <div className="item-price">{itemPrice}</div>
                  </Card.Title>
                  <Card.Text className="py-2">
                    <div className="item-description">{itemDescription}</div>
                  </Card.Text>
                </Card.Body>
              </Card>
            </Col>
          );
        })
      ) : (
        <h3 className="text-center">لا يوجد اصناف</h3>
      )}
    </Row>
  );
};

export default ItemList;
