import React, { useState } from 'react';
import { Container } from 'react-bootstrap';
import NavBar from './components/NavBar';
import Header from './components/Header';
import Category from './components/catgory';
import ItemsList from './components/ItemList';
import  MyComponent  from './data';

function App({items}) {
  const [itemsData, setItemsData] = useState(MyComponent);

  const allCategory = ['all', ...new Set(MyComponent.map((i) => i.category))];

  const filterByCategory = (cat) => {
    if (cat === 'all') {
      setItemsData(MyComponent);
    } else {
      const newArr = MyComponent.filter((items) => items.category === cat);
      setItemsData(newArr);
    }
 };


  return (
    <div className="color-body font">
      <NavBar/>
      <Container>
        <Header />
        <Category filterCategory={filterByCategory} allCategory={allCategory} />
        <ItemsList items={items} />
      </Container>
    </div>
  );
}

export default App;
