import React from "react";
import { Card, Col, Row } from "react-bootstrap";
import { useTranslation } from "react-i18next";

const ItemList = ({ itemsData }) => {
  const { t } = useTranslation();
  
  return (
    <Row>
      {itemsData && itemsData.length >= 1 ? (
        itemsData.map((item) => (
          <Col key={item.id} sm="12" className="mb-3">
            <Row>
              <Col sm="12">
                <Card className="d-flex flex-row" style={{ backgroundColor: '#F8F8F8' }}>
                  <Card.Img className="img-item" variant="top" src={item.imgUrl} />
                  <Card.Body>
                    <Card.Title className="d-flex justify-content-between">
                      <div className="item-title">{item.title} {t("all")} {t("Breakfast")} {t("Lunch")} {t("Dinner")}</div>
                      <div className="item-price">{item.price} {t("price")}</div>
                    </Card.Title>
                    <Card.Text className="py-2">
                      <div className="item-description">{item.description} {t("text")} {t("text2")} {t("text3")}</div>
                    </Card.Text>
                  </Card.Body>
                </Card>
              </Col>
            </Row>
          </Col>
        ))
      ) : (
        <h3 className="text-center">{t("noItems")}</h3>
      )}
    </Row>
  );
};

export default ItemList;
