import React from 'react';
import { useTranslation } from 'react-i18next';
import { Card, Col, Row } from 'react-bootstrap';

const ItemsList = ({ itemsData }) => {
  const { t } = useTranslation();

  return (
    <Row>
      {itemsData.length >= 1 ? (
        itemsData.map((items) => (
          <Col key={items.id} sm="12" className="mb-3">
            <Card className="d-flex flex-row" style={{ backgroundColor: '#F8F8F8' }}>
              <Card.Img className="img-item" variant="top" src={item.imgUrl} />
              <Card.Body>
                <Card.Title className="d-flex justify-content-between">
                  <div className="item-title">{t(item.title)}</div>
                  <div className="item-price">{t(item.price)}</div>
                </Card.Title>
                <Card.Text className="py-2">
                  <div className="item-description">{t(item.description)}</div>
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))
      ) : (
        <h3 className="text-center">{t('noitems')}</h3>
      )}
    </Row>
  );
};

export default ItemsList;
