import { Container } from 'react-bootstrap';
import React, { useState } from 'react';
import NavBar from '.ز/components/NavBar';
import Header from './components/Header';
import Category from './components/Category';
import ItemList from './components/ItemList';
import { items } from './data';

function App() {
  const [filteredItems, setFilteredItems] = useState(items);

  // Get all unique categories
  const allCategories = ['الكل', ...new Set(items.map((item) => item.category))];

  // Filter by category
  const filterByCategory = (category) => {
    if (category === 'الكل') {
      setFilteredItems(items);
    } else {
      const newArr = items.filter((item) => item.category === category);
      setFilteredItems(newArr);
    }
  };

  // Filter by search
  const filterBySearch = (word) => {
    if (word !== '') {
      const newArr = items.filter((item) => item.title === word);
      setFilteredItems(newArr);
    }
  };

  return (
    <div className="color-body font">
      <NavBar filterBySearch={filterBySearch} />
      <Container>
        <Header />
        <Category filterByCategory={filterByCategory} allCategory={allCategories} />
        <ItemList items={filteredItems} />
      </Container>
    </div>
  );
}

export default App;
