import React from "react";
import { Button, Col, Row } from "react-bootstrap";
import { useTranslation } from "react-i18next";

const Hi = ({ filterByCategory }) => {
  const { t } = useTranslation();

 //to filter by category
 const onFilter = (cat) => {
    filterbyCategory(cat)
}

  return (
    <Row className="my-2 mb-5">
      <Col sm="12" className="d-flex justify-content-center">
        
          <Button variant="primary" className="btn mx-2" onClick={() => onFilter("all")}>
            {t("all")}
          </Button>
          <Button variant="primary" className="btn mx-2" onClick={() => onFilter("Breakfast")}>
            {t("Breakfast")}
          </Button>
          <Button variant="primary" className="btn mx-2" onClick={() => onFilter("Lunch")}>
            {t("Lunch")}
          </Button>
          <Button variant="primary" className="btn mx-2" onClick={() => onFilter("Dinner")}>
            {t("Dinner")}
          </Button>
        
      </Col>
      <Col sm="12" className="d-flex justify-content-center">
      </Col>
    </Row>
  );
};

export default Hi;
