import React, { useState } from "react";
import NavScrollExample from "./components/NavBar";
import Header from "./components/Header";
import Hi from "./components/catgory";
import ItemList from "./components/ItemList";
import { items } from "./data";
import { useTranslation } from "react-i18next";

function App() {
  const { t } = useTranslation();
  const [itemsData, setItemsData] = useState(items)
  const allCategory = [t('all'), ...new Set(items.map((i) => i.Hi))]

  //filter by category
  const filterbyCategory = (cat) => {
    if (cat === t("all")) {
      setItemsData(items)
    } else {
      const newArr = items.filter((item) => item.Hi === cat)
      setItemsData(newArr)
    }
  }

  return (
    <div className="App">
      <NavScrollExample />
      <Header />
      <Hi filterbyCategory={filterbyCategory} allCategory={allCategory} />
      <ItemList itemsData={itemsData}/>
    </div>
  );
}

export default App;
