import React from "react";
import { Button, Col, Row } from "react-bootstrap";
import { useTranslation } from "react-i18next";
import ItemList from "./";

const Hi = ({ filterByCategory, items }) => {
  const { t } = useTranslation();

  const onFilter = (category) => {
    filterByCategory(category);
  };

  return (
    <Row className="my-2 mb-5">
      <Col sm="12" className="d-flex justify-content-center">
        <div>
          <Button className="btn mx-2" onClick={() => onFilter("all")}>
            {t("all")}
          </Button>
          <Button className="btn mx-2" onClick={() => onFilter("Breakfast")}>
            {t("Breakfast")}
          </Button>
          <Button className="btn mx-2" onClick={() => onFilter("Lunch")}>
            {t("Lunch")}
          </Button>
          <Button className="btn mx-2" onClick={() => onFilter("Dinner")}>
            {t("Dinner")}
          </Button>
        </div>
      </Col>
      <Col sm="12" className="d-flex justify-content-center">
        <ItemList items={items} />
      </Col>
    </Row>
  );
};

export default Hi;
